import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './styles/about.css';

const About = () => {
  const [counter, setCounter] = useState(0);

  const showSlide = (index) => {
    const slides = document.querySelector('.slides');
    const autoBtns = document.querySelectorAll('.auto-btn');

    slides.style.transform = `translateX(${-index * 100}%)`;
    autoBtns.forEach((btn, i) => {
      btn.style.background = i === index ? '#333' : '#ddd';
    });
  };

  useEffect(() => {
    const interval = setInterval(() => {
      setCounter((prevCounter) => (prevCounter + 1) % 3);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    showSlide(counter);
  }, [counter]);

  return (
    <div>
      <header className="header">
        <div className="logo">
          <img src="/img/Sweet Cake.png" width="150px" style={{ cursor: 'pointer' }} alt="Sweet Cake" />
        </div>
        <nav className="nav">
          <ul>
            <li><Link to="/">Home</Link></li>
            <li><Link to="/components/about">About</Link></li>
            <li><Link to="/components/products">Products</Link></li>
            <li><Link to="/components/contact">Contact Us</Link></li>
            <li><Link to="/components/info">Info</Link></li>
          </ul>
        </nav>
      </header>
      <div className="about-container">
        <div className="card">
          <h1>About Sweet Cake</h1>
          <div className="slider" id="slider">
            <div className="slides">
              <img src="/img/images (6).jpeg" alt="Slide 1" />
              <img src="/img/download - Copy.jpeg" alt="Slide 2" />
              <img src="/img/download (4).jpeg" alt="Slide 3" />
              <img src="/img/download (6).jpeg" alt="Slide 4" />
              <img src="/img/download (7).jpeg" alt="Slide 5" />
              <img src="/img/images (1) - Copy.jpeg" alt="Slide 6" />
              <img src="/img/images (4).copy.jpeg" alt="Slide 7" />
              <img src="/img/download (3).jpeg" alt="Slide 8" />
              <img src="/img/download (5).jpeg" alt="Slide 9" />
              <img src="/img/images (10).jpeg" alt="Slide 10" />
              <img src="/img/download.jpeg" alt="Slide 11" />
            </div>
          </div>
          <div className="navigation-auto">
            <button className="auto-btn" onClick={() => setCounter(0)}></button>
            <button className="auto-btn" onClick={() => setCounter(1)}></button>
            <button className="auto-btn" onClick={() => setCounter(2)}></button>
          </div>
          <div className="about-text">
            <p>Welcome to Sweet Cake, your number one source for all things sweet and delightful. We're dedicated to giving you the very best of cakes, with a focus on quality, customer service, and uniqueness.</p>
            <p>Founded in 2024 by Ikram Bachar, Sweet Cake has come a long way from its beginnings in a home kitchen. When Jane first started out, her passion for baking drove her to quit her day job, and gave her the impetus to turn hard work and inspiration into a booming online store. We now serve customers all over the country and are thrilled to be a part of the eco-friendly, fair trade wing of the baking industry.</p>
            <p>We hope you enjoy our products as much as we enjoy offering them to you. If you have any questions or comments, please don't hesitate to contact us.</p>
            <p>Sincerely,</p>
            <p>Ikram Bachar, Founder</p>
          </div>
        </div>
        <h2>Our Services</h2>
        <div className="services-container">
          <div className="service-card">
            <img src="/img/costume.jpeg" alt="Custom Cakes" />
            <h3>Custom Cakes</h3>
            <p>We create custom cakes for all your special occasions, tailored to your specifications.</p>
          </div>
          <div className="service-card">
            <img src="/img/wedding.jpeg" alt="Wedding Cakes" />
            <h3>Wedding Cakes</h3>
            <p>Our beautiful wedding cakes will make your big day even more memorable.</p>
          </div>
          <div className="service-card">
            <img src="/img/cup.jpeg" alt="Cupcakes" />
            <h3>Cupcakes</h3>
            <p>Delicious and beautifully decorated cupcakes for any event or just to enjoy.</p>
          </div>
          <div className="service-card">
            <img src="/img/class.jpeg" alt="Baking Classes" />
            <h3>Baking Classes</h3>
            <p>Join our baking classes to learn the art of baking from our expert bakers.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default About;
